                    }
                }
            }
            return null;
        }

        public WarehouseData GetItem(uint TypeID, uint Index, uint Quantity)
        {
            foreach (var Items in this)
            {
                if (Items.Header.ItemTypeID == TypeID && (Items.Header.ItemIndex == Index) && Items.Header.ItemC0 >= Quantity && Items.ItemIsValid == 1)
                {
                    return Items;
                }
            }
            return null;
        }


        public WarehouseData GetClub(uint ID, uint type)
        {
            switch (type)
            {
                case 1:
                    {
                        var ClubInfo = this.Where(c => c.Header.ItemTypeID == ID && c.ItemGroup == 4);

                        if (ClubInfo.Any())
                        {
                            return ClubInfo.FirstOrDefault();
                        }
                        else
                        {
                            return null;
                        }
                    }
                case 0:
                    {
                        var ClubInfo = this.Where(c => c.Header.ItemIndex == ID && c.ItemGroup == 4);

                        if (ClubInfo.Any())
                        {
                            return ClubInfo.FirstOrDefault();
                        }
                        else
                        {
                            return null;
                        }
                    }
            }

            return null;
        }


        public WarehouseData GetDataItem(uint ID, uint type, uint Quantity)
        {
            switch (type)
            {
                case 1:
                    {
                        var Item = this.Where(c => c.Header.ItemTypeID == ID && c.Header.ItemC0 >= Quantity && c.ItemIsValid == 1);

                        if (Item.Any())
                        {
                            return Item.FirstOrDefault();
                        }
                    }
                    break;
                case 0:
                    {
                        var Item = this.Where(c => c.Header.ItemIndex == ID && c.Header.ItemC0 >= Quantity && c.ItemIsValid == 1);

                        if (Item.Any())
                        {
                            return Item.FirstOrDefault();
                        }
                        break;
                    }
            }

            return null;
        }


